package com.example.field.Adapters;import androidx.fragment.app.Fragment;import androidx.fragment.app.FragmentManager;import androidx.fragment.app.FragmentPagerAdapter;import java.util.ArrayList;import java.util.List;public class Adapter_details extends FragmentPagerAdapter {    private final List<Fragment> fragmentList = new ArrayList<>();    private  final List<String> fragmentListTitle =  new ArrayList<>();    public Adapter_details(FragmentManager fm) {        super(fm);    }    @Override    public Fragment getItem(int position) {        return fragmentList.get(position);    }    @Override    public int getCount() {        return fragmentListTitle.size();    }    @Override    public CharSequence getPageTitle(int position) {        return fragmentListTitle.get(position);    }    public void AddFragment(Fragment fragment, String title){        fragmentList.add(fragment);        fragmentListTitle.add(title);    }}