package com.example.field.Adapters;public class Champ {    private String Maintitle;    private int images;    public Champ() {    }    public Champ(String maintitle, int images) {        Maintitle = maintitle;        this.images = images;    }    public String getMaintitle() {        return Maintitle;    }    public int getImages() {        return images;    }    public void setMaintitle(String maintitle) {        Maintitle = maintitle;    }    public void setImages(int images) {        this.images = images;    }}